import tensorflow as tffilelist = ['data/train.tfrecord']file_queue = tf.train.string_input_producer(filelist,                                            num_epochs=None,                                            shuffle=True)reader = tf.TFRecordReader()_, ex = reader.read(file_queue)feature = {    'image':tf.FixedLenFeature([], tf.string),    'label':tf.FixedLenFeature([], tf.int64)}batchsize = 2batch  = tf.train.shuffle_batch([ex], batchsize, capacity=batchsize*10,                       min_after_dequeue=batchsize*5)example = tf.parse_example(batch, features=feature)image = example['image']label = example['label']image = tf.decode_raw(image, tf.uint8)image = tf.reshape(image, [-1, 32, 32, 3])with tf.Session() as sess:    sess.run(tf.local_variables_initializer())    tf.train.start_queue_runners(sess=sess)    for i in range(1):        image_bth, _ = sess.run([image,label])        import cv2        cv2.imshow("image", image_bth[0,...])        cv2.waitKey(0)